INSERT INTO `workflow_state_extras` (`id`, `code`, `title`, `description`, `email_text`) VALUES
	(100, 'POLITICIZATION', 'Politizálás', 'Kiszedtünk belőle szövegrészt, politizálás miatt', 'Tájékoztatunk, hogy az ötletben egy szövegrészletet a közzétételt megelőzően átszerkesztettünk, vagy töröltünk. Ennek oka, hogy az oldalon beküldött ötletekben nem jelenítünk meg politikai preferenciákat. Ezzel kapcsolatban kérjük szíves megértésedet.'),
	(110, 'DATAPOLICY', 'Adatkezelés', 'Kiszedtünk belőle szövegrészt, adatkezelés miatt', 'Tájékoztatunk, hogy az ötletben egy szövegrészletet a közzétételt megelőzően adatkezelési okokból átszerkesztettünk, vagy töröltünk. Ezzel kapcsolatban kérjük szíves megértésedet.'),
	(120, 'EXCLUSIONARY', 'Kirekesztő', 'Kiszedtünk belőle szövegrészt, kirekesztés miatt', 'Tájékoztatunk, hogy az ötletben egy szövegrészletet a közzétételt megelőzően átszerkesztettünk, vagy töröltünk. Ennek oka, hogy a szövegrészletben kirekesztő módon fogalmaztál más emberekről. Ezzel kapcsolatban kérjük szíves megértésedet.'),
	(130, 'ADVERTISING', 'Reklám', 'Kiszedtünk belőle szövegrészt, reklám miatt', 'Tájékoztatunk, hogy az ötletben egy szövegrészletet a közzétételt megelőzően átszerkesztettünk, vagy töröltünk. Ennek oka, hogy az ötlet címében és leírásában nem jelenítünk konkrét termékre, személyre való utalást, reklámot. Ezzel kapcsolatban kérjük szíves megértésedet.');
